import styled from "styled-components";


export const Dropdown = styled.div`
height : 300px;
width : 70px;
display: none;
border-radius: 20px;
background:rgb(252, 245, 245);


`;
export const Img = styled.img`
width: 100%;
height: 100%;
border-radius: 50%;



`;



export const Wrapper = styled.div`
cursor: pointer;
margin-right: 14px;
width: 30px;
height : 40px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
&:hover ${Dropdown}{
    position: absolute;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

&:hover ${Img}{
    height: 30px;
    width: 30px;
}
`;
