import styled from "styled-components";


export const Dropdown = styled.div`
height : 160px;
width : 160px;
display: none;
border-radius: 20px;
background:rgb(252, 245, 245);


`;
export const Img = styled.img`
width: 100%;
height: 100%;
border-radius: 50%;
cursor: pointer;


`;



export const Wrapper = styled.div`
margin-right: 14px;
width: 40px;
height : 40px;
&:hover ${Dropdown}{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
`;
